namespace IIS.BusinessCalendar
{
    using System;
    using ICSSoft.STORMNET;
    using ICSSoft.STORMNET.Security;
    using ICSSoft.STORMNET.Business;
    using ICSSoft.STORMNET.Web.Controls;
    using ICSSoft.STORMNET.FunctionalLanguage;
    using ICSSoft.STORMNET.FunctionalLanguage.SQLWhere;
    using Resources;

    public partial class WeekL : BaseListForm<Week>
    {
        /// <summary>
        /// Конструктор без параметров,
        /// инициализирует свойства, соответствующие конкретной форме.
        /// </summary>
        public WeekL() : base(Week.Views.WeekL)
        {
            EditPage = WeekE.FormPath;
        }
                
        /// <summary>
        /// Путь до формы.
        /// </summary>
        public static string FormPath
        {
            get { return "~/forms/Week/WeekL.aspx"; }
        }

        /// <summary>
        /// Вызывается самым первым в Page_Load.
        /// </summary>
        protected override void Preload()
        {
            WebObjectListView1.Operations.OpenEditorInNewWindow = true;
            WebObjectListView1.Operations.OpenEditorInModalWindow = true;
            
            if(Request["CalendarID"] != null)
            {
                var ld = SQLWhereLanguageDef.LanguageDef;
                object calendarID = Request["CalendarID"];

                WebObjectListView1.LimitFunction = ld.GetFunction(ld.funcEQ,
                                                    new VariableDef(ld.GuidType, Information.ExtractPropertyName<Week>(w => w.Calendar)),
                                                    Request["CalendarID"]);
            }
            var sm = DataServiceProvider.DataService.SecurityManager;
            if (!(sm.AccessObjectCheck(typeof(Week), tTypeAccess.Update, false) || sm.AccessObjectCheck(typeof(Week), tTypeAccess.Full, false)))
            {
                WebObjectListView1.Operations.New = false;
                WebObjectListView1.Operations.NewByExampleInRow = false;
                WebObjectListView1.Operations.Delete = false;
                WebObjectListView1.Operations.DeleteInRow = false;
            }
        }

        /// <summary>
        /// Вызывается самым последним в Page_Load.
        /// </summary>
        protected override void Postload()
        {
        }
    }
}
